function get_media_list(e){var t=e===undefined||e%1!==0?1:e;$("#media-list").html(Theamus.Notify("spinner","Loading..."));Theamus.Ajax.run({url:Theamus.base_url+"/media/media-list&page="+t,result:"media-list",type:"include",after:function(){remove_media();var e=window.setInterval(function(){resize_admin_window()},10);setTimeout(function(){clearInterval(e)},1e4)}})}function change_media_tab(){$('[name="media-tab"]').click(function(e){e.preventDefault();update_admin_window_content("theamus-media",$(this).attr("data-file"));change_admin_window_title("theamus-media",$(this).attr("data-title"))})}function remove_media(){$(".remove").click(function(e){e.preventDefault();$("#media-result").html(Theamus.Notify("spinner","Removing..."));$("#theamus-media").scrollTop(0);var t=$(this).attr("data-id");var n=null;Theamus.Ajax.api({type:"post",url:Theamus.base_url+"/media/",method:["Media","remove_media"],data:{custom:{id:t}},success:function(e){$("#media-result").show();if(e.error.status===1){$("#media-result").html(Theamus.Notify("danger",e.error.message))}else{$("#media-result").html(Theamus.Notify("success","Removed."));get_media_list(1);if(n!==null){n=setTimeout(function(){$("#media-result").html("").hide()},1500)}}}})})}